import React from "react";
import "./App.css";
import NodeRSA from "node-rsa";
import axios from "axios";

function App() {
  const key = NodeRSA({ b: 1024 });
  let public_key = key.exportKey("public");
  let private_key = key.exportKey("private");

  console.log(public_key);
  console.log(private_key);

  function genarate() {
    localStorage.setItem("public", public_key);
    localStorage.setItem("private", private_key);
    window.location.reload();
  }

  function removeKey() {
    localStorage.removeItem("public");
    localStorage.removeItem("private");
    window.location.reload();
  }

  function encrpyt() {
    let key_private = new NodeRSA(localStorage.getItem("private"));
    let key_public = new NodeRSA(localStorage.getItem("public"));

